import React from 'react';
import { Button } from '../ui/button';

interface Report {
  id: string;
  client_name: string;
  content: string;
  created_at: string;
  conversation_id: string;
}

interface ReportViewerProps {
  report: Report;
  onClose: () => void;
}

export default function ReportViewer({ report, onClose }: ReportViewerProps) {
  // Function to format the markdown content into HTML
  const formatReportContent = (content: string) => {
    return content
      // Convert headers
      .replace(/^## (.*$)/gim, '<h2 class="text-2xl font-bold text-blue-900 mb-4 mt-6">$1</h2>')
      .replace(/^\*\*(\d+\.\s+.*?)\*\*/gim, '<h3 class="text-xl font-semibold text-gray-800 mb-3 mt-5">$1</h3>')
      .replace(/^\*\*(.*?)\*\*/gim, '<strong class="font-semibold text-gray-700">$1</strong>')
      
      // Convert bullet points
      .replace(/^\* \*\*(.*?):\*\*/gim, '<li class="mb-2"><strong class="text-gray-700">$1:</strong>')
      .replace(/^\* (.*$)/gim, '<li class="mb-2 ml-4">$1')
      
      // Convert line breaks
      .replace(/\n\n/g, '</p><p class="mb-4">')
      .replace(/\n/g, '<br>')
      
      // Wrap in paragraphs
      .replace(/^(.*)$/gim, '<p class="mb-4">$1</p>')
      
      // Clean up empty paragraphs
      .replace(/<p class="mb-4"><\/p>/g, '')
      .replace(/<p class="mb-4"><br><\/p>/g, '');
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-2xl max-w-4xl w-full max-h-[90vh] overflow-hidden">
        {/* Header */}
        <div className="bg-gradient-to-r from-blue-600 to-blue-800 text-white p-6">
          <div className="flex justify-between items-start">
            <div>
              <h1 className="text-2xl font-bold">Financial Report</h1>
              <p className="text-blue-100 mt-1">Prepared for: {report.client_name}</p>
              <p className="text-blue-100 text-sm">Generated: {formatDate(report.created_at)}</p>
            </div>
            <Button 
              onClick={onClose}
              variant="outline"
              className="bg-white text-blue-600 hover:bg-blue-50"
            >
              âœ• Close
            </Button>
          </div>
        </div>

        {/* Report Content */}
        <div className="p-6 overflow-y-auto max-h-[calc(90vh-120px)]">
          <div 
            className="report-content prose prose-lg max-w-none"
            dangerouslySetInnerHTML={{ 
              __html: formatReportContent(report.content) 
            }}
          />
        </div>

        {/* Footer */}
        <div className="bg-gray-50 px-6 py-4 border-t">
          <div className="flex justify-between items-center text-sm text-gray-600">
            <div>
              <p>Report ID: {report.id}</p>
              <p>Conversation ID: {report.conversation_id}</p>
            </div>
            <div className="text-right">
              <p>Generated by Financial Assistant AI</p>
              <p className="text-xs text-gray-500 mt-1">
                This report is for informational purposes only and does not constitute financial advice.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
