'use client';

import { Button } from '../ui/button';
import { EraserIcon, UserPlusIcon } from 'lucide-react';
import { motion } from 'framer-motion';
import { ThemeToggle } from '../ThemeToggle';
import { useState } from 'react';
import { ClientDataForm } from './ClientDataForm';

const AILogo = () => (
  <motion.div
    whileHover={{ scale: 1.05 }}
    transition={{ type: 'spring', stiffness: 300, damping: 15 }}
    className='w-12 h-12 relative'
  >
    <div className='w-12 h-12 bg-blue-600 rounded-full flex items-center justify-center'>
      <span className='text-white font-bold text-xl'>FA</span>
    </div>
    <div className='w-2 h-2 rounded-full bg-green-500 absolute -bottom-0.5 -right-0.5' />
  </motion.div>
);

interface FinancialHeaderProps {
  clearMessages: () => void;
  onGenerateReport?: () => void;
}

export default function FinancialHeader({ clearMessages }: FinancialHeaderProps) {
  const [showClientForm, setShowClientForm] = useState(false);

  const handleClientDataSubmit = async (clientData: any) => {
    try {
      const response = await fetch('/api/clients', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(clientData),
      });

      if (response.ok) {
        alert('Client data saved successfully!');
        setShowClientForm(false);
      } else {
        alert('Failed to save client data');
      }
    } catch (error) {
      alert('Error saving client data');
    }
  };

  return (
    <>
      <div className='z-10 fixed top-0 w-full p-2 px-5 mb-1 bg-background border-b border-border shadow-md'>
        <div className='flex flex-col sm:flex-row items-center w-full'>
          <div className='flex-1 flex justify-center sm:justify-start items-center gap-2'>
            <AILogo />
            <p className='text-lg font-medium'>Financial Assistant</p>
          </div>

          <div className='flex items-center space-x-3 mt-2 sm:mt-0'>
            <Button
              onClick={() => setShowClientForm(true)}
              variant='outline'
              size='sm'
              className='gap-2'
            >
              <UserPlusIcon className='w-4 h-4' />
              Add Client
            </Button>

            <ThemeToggle />

            <Button
              onClick={clearMessages}
              className='gap-2'
              variant='outline'
              size='sm'
            >
              <EraserIcon className='w-4 h-4' />
              Clear
            </Button>
          </div>
        </div>
      </div>

      {showClientForm && (
        <div className='fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50'>
          <div className='bg-background border border-border p-6 rounded-lg max-w-md w-full m-4 shadow-lg'>
            <div className='flex justify-between items-center mb-4'>
              <h2 className='text-xl font-semibold'>Add New Client</h2>
              <Button
                variant='ghost'
                size='sm'
                onClick={() => setShowClientForm(false)}
              >
                ✕
              </Button>
            </div>
            <ClientDataForm
              onSubmit={handleClientDataSubmit}
            />
          </div>
        </div>
      )}
    </>
  );
}
