'use client';

import { TooltipProvider } from '@radix-ui/react-tooltip';
import ChatMessages from '../chat/messages';
import ChatInput from '../chat/input';
import FinancialHeader from './FinancialHeader';
import useFinancialApp from '../../hooks/useFinancialApp';

export default function FinancialApp() {
  const {
    messages,
    handleInputChange,
    handleSubmit,
    indicatorState,
    input,
    isLoading,
    clearMessages,
    conversationId,
  } = useFinancialApp();

  const [currentReport, setCurrentReport] = useState(null);

  const handleGenerateReport = async () => {
    try {
      // For now, we'll use a default client name. In a real app, you'd get this from user input or context
      const clientName = prompt("Enter client name for the financial report:");
      if (!clientName) return;

      const response = await fetch('http://localhost:8000/api/generate-report', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          client_name: clientName,
          conversation_id: conversationId,
          report_type: 'comprehensive'
        }),
      });

      if (response.ok) {
        const result = await response.json();

        // Fetch the full report data
        const reportResponse = await fetch(result.report_url);
        if (reportResponse.ok) {
          const reportData = await reportResponse.json();
          setCurrentReport(reportData.report);
        } else {
          alert('Failed to load report data');
        }
      } else {
        alert('Failed to generate financial report');
      }
    } catch (error) {
      console.error('Error generating report:', error);
      alert('Error generating financial report');
    }
  };

  return (
    <TooltipProvider>
      <div className='flex flex-col h-screen bg-background'>
        <FinancialHeader clearMessages={clearMessages} onGenerateReport={handleGenerateReport} />

        <div className='flex-1 overflow-y-auto pt-16'>
          <ChatMessages
            messages={messages}
            indicatorState={indicatorState}
          />
        </div>

        <ChatInput
          handleInputChange={handleInputChange}
          handleSubmit={handleSubmit}
          input={input}
          isLoading={isLoading}
        />

        {/* Report Viewer Modal */}
        {currentReport && (
          <ReportViewer 
            report={currentReport} 
            onClose={() => setCurrentReport(null)} 
          />
        )}
      </div>
    </TooltipProvider>
  );
}
