import React from 'react';
import { DisplayMessage, LoadingIndicator } from '../../types';

interface ChatMessagesProps {
  messages: DisplayMessage[];
  indicatorState: LoadingIndicator[];
}

const ChatMessages: React.FC<ChatMessagesProps> = ({ messages, indicatorState }) => {
  return (
    <div className="flex-1 overflow-y-auto p-4 space-y-4">
      {messages.map((msg, index) => (
        <div key={index} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
          <div className={`max-w-[80%] p-3 rounded-lg ${
            msg.role === 'user' 
              ? 'bg-primary text-primary-foreground' 
              : 'bg-muted text-muted-foreground'
          }`}>
            <div className="text-sm font-medium mb-1 capitalize">{msg.role}</div>
            <div className="whitespace-pre-wrap">{msg.content}</div>
            {msg.citations && msg.citations.length > 0 && (
              <div className="mt-2 pt-2 border-t border-border">
                <div className="text-xs font-medium mb-1">Sources:</div>
                {msg.citations.map((citation, idx) => (
                  <div key={idx} className="text-xs">
                    <a href={citation.source_url} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:underline">
                      {citation.source_description}
                    </a>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      ))}
      {indicatorState.map((indicator, index) => (
        <div key={`indicator-${index}`} className="flex justify-start">
          <div className="bg-muted text-muted-foreground p-3 rounded-lg">
            <div className="flex items-center gap-2">
              <div className="w-2 h-2 bg-blue-500 rounded-full animate-pulse"></div>
              <em className="text-sm">{indicator.status}</em>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ChatMessages;
