import React from 'react';
import { DisplayMessage, LoadingIndicator } from '../../types';

interface ChatMessagesProps {
  messages: DisplayMessage[];
  indicatorState: LoadingIndicator[];
}

const ChatMessages: React.FC<ChatMessagesProps> = ({ messages, indicatorState }) => {
  return (
    <div className="flex-1 overflow-y-auto p-6 space-y-6 bg-gray-50 chat-scroll">
      {messages.map((msg, index) => (
        <div key={index} className={`flex items-start gap-3 chat-message ${msg.role === 'user' ? 'flex-row-reverse' : 'flex-row'}`}>
          {/* Avatar */}
          <div className={`flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
            msg.role === 'user' 
              ? 'bg-blue-500 text-white' 
              : 'bg-gray-200 text-gray-600'
          }`}>
            {msg.role === 'user' ? 'ðŸ‘¤' : 'ðŸ¤–'}
          </div>

          {/* Message Bubble */}
          <div className={`max-w-[70%] ${msg.role === 'user' ? 'flex flex-col items-end' : 'flex flex-col items-start'}`}>
            <div className={`px-4 py-3 rounded-2xl shadow-sm ${
              msg.role === 'user' 
                ? 'bg-blue-500 text-white rounded-br-md' 
                : 'bg-white text-gray-800 rounded-bl-md border border-gray-200'
            }`}>
              <div className="whitespace-pre-wrap text-sm leading-relaxed">{msg.content}</div>
              {msg.citations && msg.citations.length > 0 && (
                <div className="mt-3 pt-3 border-t border-gray-200">
                  <div className="text-xs font-medium mb-2 text-gray-600">Sources:</div>
                  {msg.citations.map((citation, idx) => (
                    <div key={idx} className="text-xs">
                      <a href={citation.source_url} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:underline">
                        {citation.source_description}
                      </a>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Copy button */}
            <button 
              className="mt-1 p-1 text-gray-400 hover:text-gray-600 transition-colors"
              onClick={() => navigator.clipboard.writeText(msg.content)}
              title="Copy message"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 16H6a2 2 0 01-2-2V6a2 2 0 012-2h8a2 2 0 012 2v2m-6 12h8a2 2 0 002-2v-8a2 2 0 00-2-2h-8a2 2 0 00-2 2v8a2 2 0 002 2z" />
              </svg>
            </button>
          </div>
        </div>
      ))}

      {/* Loading indicators */}
      {indicatorState.map((indicator, index) => (
        <div key={`indicator-${index}`} className="flex items-start gap-3">
          <div className="flex-shrink-0 w-8 h-8 rounded-full bg-gray-200 text-gray-600 flex items-center justify-center text-sm font-medium">
            ðŸ¤–
          </div>
          <div className="bg-white border border-gray-200 px-4 py-3 rounded-2xl rounded-bl-md shadow-sm">
            <div className="flex items-center gap-2">
              <div className="flex space-x-1">
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
              </div>
              <em className="text-sm text-gray-600">{indicator.status}</em>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ChatMessages;
