import { useState } from "react";
import { Button } from "../ui/button";
import { Input } from "../ui/input";
import { Label } from "../ui/label";
import { Textarea } from "../ui/textarea";
import { Select } from "../ui/select";

interface ClientData {
  name: string;
  age: number | null;
  income: number | null;
  risk_tolerance: string;
  financial_goals: string;
}

interface ClientDataFormProps {
  onSubmit: (data: ClientData) => void;
  initialData?: Partial<ClientData>;
}

export function ClientDataForm({ onSubmit, initialData = {} }: ClientDataFormProps) {
  const [formData, setFormData] = useState<ClientData>({
    name: initialData.name || "",
    age: initialData.age || null,
    income: initialData.income || null,
    risk_tolerance: initialData.risk_tolerance || "",
    financial_goals: initialData.financial_goals || "",
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4 p-4 border border-border rounded-lg bg-background">
      <h3 className="text-lg font-semibold text-foreground">Client Information</h3>

      <div>
        <Label htmlFor="name">Client Name</Label>
        <Input
          id="name"
          value={formData.name}
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          required
        />
      </div>

      <div>
        <Label htmlFor="age">Age</Label>
        <Input
          id="age"
          type="number"
          value={formData.age || ""}
          onChange={(e) => setFormData({ ...formData, age: e.target.value ? parseInt(e.target.value) : null })}
        />
      </div>

      <div>
        <Label htmlFor="income">Annual Income</Label>
        <Input
          id="income"
          type="number"
          step="1000"
          value={formData.income || ""}
          onChange={(e) => setFormData({ ...formData, income: e.target.value ? parseFloat(e.target.value) : null })}
        />
      </div>

      <div>
        <Label htmlFor="risk_tolerance">Risk Tolerance</Label>
        <Select
          id="risk_tolerance"
          value={formData.risk_tolerance}
          onChange={(e) => setFormData({ ...formData, risk_tolerance: e.target.value })}
        >
          <option value="" disabled>Select risk tolerance</option>
          <option value="conservative">Conservative</option>
          <option value="moderate">Moderate</option>
          <option value="aggressive">Aggressive</option>
        </Select>
      </div>

      <div>
        <Label htmlFor="financial_goals">Financial Goals</Label>
        <Textarea
          id="financial_goals"
          value={formData.financial_goals}
          onChange={(e) => setFormData({ ...formData, financial_goals: e.target.value })}
          placeholder="Retirement, home purchase, education funding, etc."
        />
      </div>

      <Button type="submit">Save Client Data</Button>
    </form>
  );
}
